package org.tnsif.operator;

public class Bitwise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int a=8;
    int b=1;
    System.out.println(a&b);
    System.out.println(a|b);
    System.out.println(a^b);
    System.out.println(a<<b);
    System.out.println(a>>b);
    
	}

}
