package org.tnsif.operator;

public class IncreDecre {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   int a=10;
   int b=2;
  int X=a++;
   System.out.println(X);
   int y=++b;
   System.out.println(y);
   int z=a--;
   System.out.println(z);
   int p=--b;
   System.out.println(p);
	}

}
